import React, { useState } from 'react';

function CarouselSection() {
  const [activeIndex, setActiveIndex] = useState(0);

  const getRandomImage = () => {
    const imageNumber = Math.floor(Math.random() * 1000) + 1;
    return `https://placekitten.com/800/400?image=${imageNumber}`;
  };

  const handleSlideChange = (selectedIndex) => {
    setActiveIndex(selectedIndex);
  };

  return (
    <div id="carouselExampleCaptions" className="carousel slide" data-bs-ride="carousel">
      <div className="carousel-indicators">
        {[0, 1, 2].map((index) => (
          <button
            key={index}
            type="button"
            data-bs-target="#carouselExampleCaptions"
            data-bs-slide-to={index}
            className={index === activeIndex ? 'active' : ''}
            onClick={() => handleSlideChange(index)}
            aria-current={index === activeIndex}
            aria-label={`Slide ${index + 1}`}
          />
        ))}
      </div>
      <div className="carousel-inner">
        {[0, 1, 2].map((index) => (
          <div key={index} className={`carousel-item ${index === activeIndex ? 'active' : ''}`}>
            <img src={getRandomImage()} className="d-block w-100" alt={`Slide ${index + 1}`} />
            <div className="carousel-caption d-none d-md-block">
              <h5>{`Slide ${index + 1} label`}</h5>
              <p>Some representative placeholder content for the slide.</p>
            </div>
          </div>
        ))}
      </div>
      <button
        className="carousel-control-prev"
        type="button"
        data-bs-target="#carouselExampleCaptions"
        data-bs-slide="prev"
        onClick={() => handleSlideChange((activeIndex - 1 + 3) % 3)}
      >
        <span className="carousel-control-prev-icon" aria-hidden="true" />
        <span className="visually-hidden">Previous</span>
      </button>
      <button
        className="carousel-control-next"
        type="button"
        data-bs-target="#carouselExampleCaptions"
        data-bs-slide="next"
        onClick={() => handleSlideChange((activeIndex + 1) % 3)}
      >
        <span className="carousel-control-next-icon" aria-hidden="true" />
        <span className="visually-hidden">Next</span>
      </button>
    </div>
  );
}

export default CarouselSection;
