import React, { useRef } from 'react';
import Sidebar from './components/Sidebar';
import HeroSection from './components/HeroSection';
import AboutSection from './components/AboutSection';
import ImageGallerySection from './components/ImageGallerySection';
import ContactSection from './components/ContactSection';
import TestimonialsSection from './components/TestimonialsSection';

function App() {
  const testimonialsSectionRef = useRef(null);

  const handleButtonClick = (section) => {
    switch (section) {
      case 'home':
        window.scrollTo({ top: 0, behavior: 'smooth' });
        break;
      case 'about':
        window.scrollTo({ top: aboutSectionRef.current.offsetTop, behavior: 'smooth' });
        break;
      case 'images':
        window.scrollTo({ top: imageGallerySectionRef.current.offsetTop, behavior: 'smooth' });
        break;
      case 'testimonials':
        window.scrollTo({ top: testimonialsSectionRef.current.offsetTop, behavior: 'smooth' });
        break;
      case 'contact':
        window.scrollTo({ top: contactSectionRef.current.offsetTop, behavior: 'smooth' });
        break;
      default:
        break;
    }
  };

  return (
    <div className="flex">
      <Sidebar handleButtonClick={handleButtonClick} />
      <div className="container-main flex-1">
        <HeroSection />
        <AboutSection ref={aboutSectionRef} />
        <ImageGallerySection ref={imageGallerySectionRef} />
        <TestimonialsSection ref={testimonialsSectionRef} />
        <ContactSection ref={contactSectionRef} />
      </div>
    </div>
  );
}

export default App;
