// Sidebar.jsx

import React from 'react';
import { FaHome, FaInfoCircle, FaImage, FaComments, FaEnvelope } from 'react-icons/fa';
import { Link } from 'react-router-dom';

import logo from '@/img/logo.svg';

function Sidebar({ show, handleButtonClick }) {
  const SidebarItem = ({ icon, name, route }) => {
    const isActive = window.location.pathname === route;

    return (
      <Link
        to={route}
        onClick={() => handleButtonClick(route)}
        className={`nav-button flex items-center focus:outline-none pl-6 py-3 border-b-[1px] border-b-white/10 ${isActive ? 'text-white' : 'text-white/50 hover:text-white'}`}
      >
        <div className="text-xl flex mx-auto w-[30px]">{icon}</div>
        <div>{name}</div>
      </Link>
    );
  };

  return (
    <>
      <div className={`bg-black w-[250px] transition-[margin-left] ease-in-out duration-500 fixed md:static top-0 bottom-0 left-0 z-40 ${show ? 'ml-0' : 'ml-[-250px] md:ml-0'}`}>
        <div className="p-2 flex">
          <Link to="/">
            <img src={logo.src} alt="Company Logo" width={300} height={300} />
          </Link>
        </div>
        <div className="flex flex-col">
          <SidebarItem name="Home" route="/" icon={<FaHome />} />
          <SidebarItem name="About Us" route="/about" icon={<FaInfoCircle />} />
          <SidebarItem name="Images" route="/images" icon={<FaImage />} />
          <SidebarItem name="Testimonials" route="/testimonials" icon={<FaComments />} />
          <SidebarItem name="Contact Us" route="/contact" icon={<FaEnvelope />} />
        </div>
      </div>
      {show && <div className="flex md:hidden fixed top-0 right-0 bottom-0 left-0 bg-black/50 z-30" onClick={() => handleButtonClick()} />}
    </>
  );
}

export default Sidebar;
