    <div className="bg-white py-10">
  <div className="px-6">
    <div className="flex flex-wrap justify-between items-center">
     
      {/* Ensure vertical alignment */}
      {/* Contact Info */}
      <div className="w-full lg:w-1/2 mb-6 lg:mb-0">
        <h2 className="text-4xl font-semibold text-gray-800 mb-6">
          Contact Us
        </h2>{" "}
        {/* Match heading level and styling */}
        <h3 className="text-3xl font-semibold text-gray-800 mb-6">
          We'd love to hear from you!
        </h3>{" "}
        {/* Match styling for subheading */}
        <p className="text-gray-600 mb-4">
          Have a question about our services? Want to see if your particular
          vacation is one we can help with? We'd love to hear from you!
        </p>
        <button className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 focus:outline-none flex items-center">
          {" "}
          {/* Flex container for button contents */}
          <i className="fab fa-whatsapp mr-2" />
          Message us on WhatsApp
        </button>
        <div className="mt-8 text-gray-600">
          {" "}
          {/* Use the same text color for the entire block */}
          <h4 className="text-xl font-semibold mb-2">travel</h4>{" "}
          {/* Ensure heading level and styling */}
          <p>Hours</p>
          <p>Open today 09:00 am â€“ 05:00 pm</p>
        </div>
      </div>
      {/* Image */}
      <div className="w-full lg:w-1/2">
        <img
          className="rounded-lg"
          src="https://placehold.co/500x300?text=Rice+Terraces+Placeholder"
          alt="Rice terraces"
        />
      </div>
    </div>
  </div>
</div>

  );
}

export default ContactSection;
