import React, { useRef } from 'react';
import Sidebar from './components/Sidebar';
import HeroSection from './components/HeroSection';
import AboutSection from './components/AboutSection';
import ImageGallerySection from './components/ImageGallerySection';
import ContactSection from './components/ContactSection';
import TestimonialsSection from './components/TestimonialsSection';

function App() {
  const aboutSectionRef = useRef(null);
  const imageGallerySectionRef = useRef(null);
  const contactSectionRef = useRef(null);

  const handleButtonClick = (sectionRef) => {
    const targetRef = sectionRef.current;
    if (targetRef) {
      window.scrollTo({ top: targetRef.offsetTop, behavior: 'smooth' });
    }
  };

  return (
    <div className="flex">
      <Sidebar handleButtonClick={handleButtonClick} />
      <div className="container-main flex-1">
        <HeroSection />
        <AboutSection sectionRef={aboutSectionRef} />
        <ImageGallerySection sectionRef={imageGallerySectionRef} />
        <TestimonialsSection />
        <ContactSection sectionRef={contactSectionRef} />
      </div>
    </div>
  );
}

export default App;
